import React, {useEffect, useReducer} from 'react';
import axios from 'axios';
import requestReducer, { REQUEST_STATUS } from '../../reducers/request';
import {
    PUT_SUCCESS,
    PUT_FAILURE,
    GET_ALL_SUCCESS,
    GET_ALL_FAILURE,
} from '../../actions/request';

const withRequest = (baseUrl, routeName) => (Component) => () => {

    const [{records: speakers, status, error}, dispatch] = useReducer(requestReducer, {
        records: [],
        status: REQUEST_STATUS.LOADING,
        error: null,
    });

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get(`${baseUrl}/${routeName}`);
                dispatch({
                    records: response.data,
                    type: GET_ALL_SUCCESS,
                });
            } catch (e) {
                dispatch({
                    error: e,
                    type: GET_ALL_FAILURE,
                });
            }
        }
        fetchData();
    }, [baseUrl, routeName ]);

    const props = {
        records,
        status,
        error,
        put: async (record) => {
            try {
                await axios.put(`http://localhost:4000/speakers/${speakerRec.id}`, toggledSpeakerRec);
                dispatch({
                    type: PUT_SUCCESS,
                    record: record,
                });
            } catch (e) {
                dispatch({
                    type: PUT_FAILURE,
                    error: e,
                });
            }
        }
    }

    return <Component {...props}></Component>;

};
export default withRequest;
