import React from 'react'
import withData from './withData'

const Speakers = ({speakers}) => {
    return (
        <div>
            <div className='mb-6'>
                <input id='username' type='text' placeholder='Search by name' />
            </div>
            <div className='grid md:grid-cols-2 lg:grid-cols-3 grid-cols-1 gap-12'>
                {speakers.map(( { id, firstName, lastName, bio, isFavorite}) => {
                    <div className=''>
                        <div className=''>
                            <div className=''>{`${firstName} ${lastName}`}</div>
                            <div className=''>
                                <div className={isFavorite ? 'heartredbutton' : 'heartdarkbutton'} />
                            </div>
                        </div>
                        <div className=''>
                            <img src={`/speakers/speaker-${id}.jpg`} alt={`${firstName} ${lastName}`} />
                        </div>
                    </div>
                })}
            </div>
        </div>
    );
};
const maxSpeakersToShow = 3;
export default withData(maxSpeakersToShow)(Speakers);
